mapper.update:
UPDATE user
SET last_login_time = #{loginTime},
    last_login_ip = #{loginIp}
WHERE id = #{userId}

UPDATE user
SET update_time = now(),
    nickname = #{nickname},
    sex = #{sex},
    signature = #{signature}
WHERE id = #{userId}

UPDATE user
SET update_time = now(),
    password = #{newPassword}
WHERE id = #{userId}
